package JavaConscepts;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//primitive data types boolean, sort, int, long, double char
		int i=10;
		i=20;
		i=30;
		int j=40;
		int k= -10;
		int p=0;
		System.out.println(i);
		System.out.println(k);
		System.out.println(i+j+"\n \n");
		
		//double
		double d1= 12.33;
		double d2=-12.33;
		double d3= 100;
		System.out.println(d1+d2);
		System.out.println(d3);
		
		//3. char
		char c1='a';
		char c2='1';
		char c3='$';
		char gender='X';
		System.out.println("\n char print \n"+c1+"\n"+c2+"\n"+c3+"\n"+gender);
		
		//4. boolean
		boolean b1= true;
		boolean b2= false;
		
		//5. String is class in java, not a data type
		String s1="hello World";
		String s2="Hey this is my new tutorials";
		String s3="100";
		String s4= "12.66";
		System.out.println("\n Strings print \n"+s1+"\n"+s2+"\n"+s3+"\n"+s4);
		System.out.println("Hello World");
		
	}

}
